INSERT INTO `wp_snippets` (`id`, `name`, `description`, `code`, `tags`, `scope`, `priority`, `active`, `modified`) VALUES
(1, 'Make upload filenames lowercase', 'Makes sure that image and file uploads have lowercase filenames.\n\nThis is a sample snippet. Feel free to use it, edit it, or remove it.', 'add_filter( \'sanitize_file_name\', \'mb_strtolower\' );', 'sample, media', 'global', 10, 0, '2023-07-11 17:09:21'),
(2, 'Disable admin bar', 'Turns off the WordPress admin bar for everyone except administrators.\n\nThis is a sample snippet. Feel free to use it, edit it, or remove it.', 'add_action( \'wp\', function () {\n	if ( ! current_user_can( \'manage_options\' ) ) {\n		show_admin_bar( false );\n	}\n} );', 'sample, admin-bar', 'front-end', 10, 0, '2023-07-11 17:09:21'),
(3, 'Allow smilies', 'Allows smiley conversion in obscure places.\n\nThis is a sample snippet. Feel free to use it, edit it, or remove it.', 'add_filter( \'widget_text\', \'convert_smilies\' );\nadd_filter( \'the_title\', \'convert_smilies\' );\nadd_filter( \'wp_title\', \'convert_smilies\' );\nadd_filter( \'get_bloginfo\', \'convert_smilies\' );', 'sample', 'global', 10, 0, '2023-07-11 17:09:21'),
(4, 'Current year', 'Shortcode for inserting the current year into a post or page..\n\nThis is a sample snippet. Feel free to use it, edit it, or remove it.', '<?php echo date( \'Y\' ); ?>', 'sample, dates', 'content', 10, 0, '2023-07-11 17:09:21'),
(5, 'redirect_to_required_product page if not in cart - (woocommerce_before_checkout_form)', '<p>redirect_to_required_product page if not in cart - (woocommerce_before_checkout_form)</p>\n<p>$required_product_id        have to be set</p>', 'function redirect_to_required_product() {\n    // Set the required product ID\n    $required_product_id = 147;\n\n    // Check if the required product is in the cart\n    $found_product = false;\n    foreach ( WC()->cart->get_cart() as $cart_item ) {\n        $product_id = $cart_item[\'product_id\'];\n        if ( $product_id == $required_product_id ) {\n            $found_product = true;\n            break;\n        }\n        // For booking products, check the variation ID as well\n        if ( $cart_item[\'variation_id\'] == $required_product_id ) {\n            $found_product = true;\n            break;\n        }\n    }\n\n    // Redirect to the required product page if not found in the cart\n    if ( ! $found_product ) {\n        wp_redirect( get_permalink( $required_product_id ) );\n        exit();\n    }\n}\nadd_action( \'woocommerce_before_checkout_form\', \'redirect_to_required_product\' );\n', '', 'global', 10, 0, '2023-07-11 17:16:24');
